import React from "react";
import NavBar from "./NavBar";

import PatientService from "../services/PatientServices";
import { Col, Row } from "react-bootstrap";

class ViewPateient extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      patients: [],
    };
  }

  componentDidMount() {
    PatientService.getPatients().then((response) => {
      this.setState({ patients: response.data });
      const bool = response.data.anOutPatient;
      const str = bool === true ? "Yes" : "No";
      response.data.anOutPatient = str;
      // const bool = true;
      console.log(str);
    });
  }
  render() {
    return (
      <div>
        <NavBar />
        <Container>
          <Row>
            <Col>
              <Button variant="primary" onClick={handleShow}>
                Launch demo modal
              </Button>

              <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                  <Modal.Title>Modal heading</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                  Woohoo, you're reading this text in a modal!
                </Modal.Body>
                <Modal.Footer>
                  <Button variant="secondary" onClick={handleClose}>
                    Close
                  </Button>
                  <Button variant="primary" onClick={handleClose}>
                    Save Changes
                  </Button>
                </Modal.Footer>
              </Modal>
            </Col>
          </Row>
        </Container>
        <h2 className="text-center">List Of All Registered Patients</h2>

        <table className="table table-striped">
          <thead>
            <tr>
              <td>#</td>
              <td>Patient Number</td>
              <td>Patient Name</td>
              <td>Patient Email</td>
              <td>Patient Phone Number</td>
              <td>Patient Date of Birth</td>
              <td>Out patient?</td>
            </tr>
          </thead>
          <tbody>
            {this.state.patients.map((patient) => (
              <tr key={patient.patientId}>
                <td>{patient.patientId}</td>
                <td>{patient.patientNumber}</td>
                <td>{patient.fullNames}</td>
                <td>{patient.emailAddress}</td>
                <td>{patient.contactPhoneNumber}</td>
                <td>{patient.dateOfBirth}</td>
                <td>{String(patient.anOutPatient)}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  }
}
export default ViewPateient;
